import React from "react";
import axios from "axios";
import Layout from "../../components/Layout";
import { Container } from "reactstrap";

class About extends React.Component {
  static async getInitialProps() {
    const { data } = await axios.get("http://localhost:3000/api/about/1");
    return { data };
  }
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <Layout >
        <Container>
          <h3 className="text-right mt-5">{this.props.data.title}</h3>
          <div
          className="article-body post-body text-right"
          dangerouslySetInnerHTML={{ __html: this.props.data.body }}
        ></div>
        </Container>
      </Layout>
    );
  }
}

export default About;
