import React from "react";
import Layout from "../components/Layout";
import axios from "axios";

import { Container, Row, Col } from "reactstrap";
const navies = [
  {
    id: 1,
    number: 737,
    name: "Boeing"
  },
  { id: 2, number: 320, name: "Airbus" }
];

class Navy extends React.Component {
  static async getInitialProps() {
    const { data } = await axios.get("http://localhost:3000/api/news");
    return { data };
  }

  constructor(props) {
    super(props);
    this.state = { selected: navies[0] };
  }

  render() {
    const { selected } = this.state;
    return (
      <Layout page="article">
        <Container>
          <Row>
            <Col sm="12">
              <Row className="rtl">
                <Col sm="8">
                  <img
                    src="/static/images/airbus320.png"
                    className="navy-image"
                  />
                </Col>
                <Col sm="4" className="text-right rtl">
                  <h5>بوئینگ 737</h5>
                  <p>
                    بوئینگ 737 معروف به بچه‌بوئینگ، یک هواپیمای مسافربری
                    باریک‌پیکر و تک‌راهرو ساخته شرکت بوئینگ آمریکا است. طراحی
                    این هواپیما از 11 می 1964 آغاز شد و نخستین‌بار در 9 آوریل
                    1967 به پرواز درآمد.
                  </p>
                </Col>
              </Row>
            </Col>
            <Col sm="12">
              <div className="changer">
                <Row className="justify-content-center">
                  <Col sm="2">
                    <button className={selected == 1 ? "active" : "deactive"}>
                      1
                    </button>
                  </Col>
                  <Col sm="2">
                    <button className={selected == 2 ? "active" : "deactive"}>
                      2
                    </button>
                  </Col>
                </Row>
              </div>
            </Col>
          </Row>
        </Container>
      </Layout>
    );
  }
}

export default Navy;
