import React from "react";
import Layout from "../components/Layout";
import axios from "axios";

import { Container, Row, Col } from "reactstrap";

class News extends React.Component {
  static async getInitialProps() {
    const { data } = await axios.get("http://localhost:3000/api/news");
    console.log(data);
    return { data };
  }

  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <Layout page="article">
        <Container>
          {this.props.data.map((item, index) => {
            return (
              <div className="shadow-lg bg-white rounded mt-2 mb-2">
                <Row>
                  <Col>
                    <h5 className="p-2"> {item.title}</h5>
                    <p className="text-secondary">{item.body}</p>;
                  </Col>
                  <Col></Col>
                </Row>
              </div>
            );
          })}
        </Container>
      </Layout>
    );
  }
}

export default News;
